;(function($) {

  function Tagalong(opts) {

    this.opts = $.extend({
        article_selector: 'article',
        aside_selector: 'aside'
    }, opts || {});

    this.update_cache();
    this.$win = $(window).on('scroll', $.proxy(this.scroll_handler, this));
  }

  Tagalong.prototype.update_cache = function() {
    this.cache = [];

    var $articles = $(this.opts.article_selector),
        total_articles = $articles.size(),
        $article,
        $aside;

    for (var i = 0; i < total_articles; i++) {

      $article = $articles.filter(':eq(' + i + ')'),
      $aside = $article.find(this.opts.aside_selector);

      $article.css({ position: 'relative' });
      $aside.css({ position: 'absolute', top: '0px', bottom:'' });

      this.cache.push({
        article: {
          $elem: $article,
          height: $article.height(),
          offset: $article.offset(),
          topPadding: $article.css('paddingTop'),
          bottomPadding: $article.css('paddingBottom')
        },
        aside: {
          $elem: $aside,
          height: $aside.outerHeight(true)
        }
      });
    }
  }

  Tagalong.prototype.scroll_handler = function() {

    var article,
        aside,
        article_top,
        css;

    for(var i = 0; i < this.cache.length; i++) {
      article = this.cache[i].article;
      aside = this.cache[i].aside;
      article_top = article.offset.top - this.$win.scrollTop();
      css = { };

      if (article_top + (article.height - aside.height) <= 25) {
        css = { width: aside.$elem.parent().width(), paddingTop: 0, position: 'absolute', top: '', bottom: article.bottomPadding };
      } else if (article_top < 25) {
        css = { paddingTop: 25, position: 'fixed', top: article.topPadding, bottom: '' };
      } else {
        css = { width: aside.$elem.parent().width(), paddingTop: 0, position: 'absolute', top: article.topPadding, bottom: '' };
      }

      aside.$elem.css(css);
    }
  }

  $.extend({
    tagalong: function(opts) {
      return new Tagalong(opts);
    }
  });

})(jQuery);




